INSERT INTO ANIMAL (NAME, CLASS_TYPE, ORDER_TYPE, FAMILY, DESCRIPTION, REFERENCE) VALUES
('Lion', 'Mammals', 'Predatory', 'Felines',
'The lion (Panthera leo) is a large felid of the genus Panthera. It has a muscular, deep-chested body, short, rounded head, round ears, and a hairy tuft at the end of its tail. It is sexually dimorphic; adult male lions have a prominent mane. With a typical head-to-body length of 184 to 208 cm (72 to 82 in) they are larger than females at 160 to 184 cm (63 to 72 in).',
'https://en.wikipedia.org/wiki/Lion');